
public class FloatDouble {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int myIntValue=5/3;
		System.out.println(myIntValue);
		
		float myFloatValue=5f/3f;
		System.out.println(myFloatValue);
		
		double myDoubleValue=5d/3d;
		System.out.println(myDoubleValue);
		
		//Challenge
		//Convert a given number of pounds to kilogram
		//1. Create a variable to store the no of pounds;
		 double myPoundValue=200d;
		 //2 Calculate the number of kilograms for the number and store in a variable
		  
		 double kilogramsValue=myPoundValue*0.45359237d;
		 
		 //3 Print out the Result
		 
		 System.out.println("my KilogramValue="+kilogramsValue);
	} 

}
