
public class OperatorTwo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
    int result=20;
    result++;
    System.out.println(result);
    result--;
    System.out.println(result);
    
    int myResultValue=30;
    myResultValue+=10;
    System.out.println("Result is now:"+myResultValue);
    myResultValue-=20;
    System.out.println("Result is now:"+myResultValue);
    
    myResultValue*=10;
    System.out.println("Result is now:"+myResultValue);
    
    
	}

}
